package fr.masterdapm.toulon.item;

public class ObjectDrawerItem {
    public int icon;
    public String name;
 
    // Constructor.
    public ObjectDrawerItem(int icon, String name) {
 
        this.icon = icon;
        this.name = name;
    }

	public int getIcon() {
		// TODO Auto-generated method stub
		return icon;
	}
	public String getName() {
		// TODO Auto-generated method stub
		return name; 
	}
}
